import React, {Component} from 'react';
import './login.css';
import logo from './logo.svg';
import Login,{login} from './login';


function LoadLogin(props){
    return (

        <Login/>

        );    
}

function LoadSignup(props){

    return(

        <div className="signup">
        <div className="signup-header">
            <img src={logo} className="App-logo" alt="logo" />
              <h2>SIGN IN</h2>    
        </div>
        <div className="signup-body">
        <form>
          <label>Name</label>
          <input type="text" name="name" onChange={props.saveText}/>
        <br/>
        <label>Surnames</label>
        <input type="text" name="surnames"onChange={props.saveText} />
      <br/>
      <label>Username</label>
        <input type="text" name="username" onChange={props.saveText}/>
      <br/>
      <label>Email</label>
        <input type="text" name="email" onChange={props.saveText}/>
      <br/>
      <label>Password</label>
      <input type="password" name="password" onChange={props.saveText}/>
      <br/>
      <label>Confirm Password</label>
      <input type="password" name="confirm" onChange={props.saveText}/>
      <br/>
          <input type="submit" value="Submit" onChange={props.saveText}/>
      </form>
      </div>
      <div className="signup-footer">
         <a href="#" onClick={props.loadLogin}>
            Cancel
          </a>
      </div>
    </div>

    );
}


class signup extends Component{

    constructor(props){
        super(props);
        this.state={
            names:"",
            surnames:"",
            username:"",
            email:"",
            password:"",
            confirm_password:"",
            clicked:true,
        }
        this.handleSignupClick= this.handleSignupClick.bind(this);  
        this.ChangeHandler=this.ChangeHandler.bind(this);

    }

    handleSignupClick() {
        this.setState(state => ({
          clicked: !state.clicked
        }));
        alert(this.state);
      }


      ChangeHandler(event){

        this.setState({
        [event.target.name]:event.target.value
        })


      }


  render(){



    return (

        <div>
            {this.state.clicked ?  <LoadSignup loadLogin={this.handleSignupClick} saveText={this.ChangeHandler}/> :<LoadLogin/>}    
        </div>


    );


  }

}

export default signup;
