import React, {Component} from 'react';
import './login.css';
import logo from './logo.svg';
import Signup,{signup} from './signup';


function LoadSign(props){


  return (

    <Signup/>

    );
}


function LoadLogin(props){


  return (      
    <div className="login">
      <div className="login-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h2>LOGIN</h2>    
      </div>
      <div className="login-body">
        <form>
        <label>Username</label>
        <input type="text" username="username" onChange={(e) => props.saveText(e)}/>
        <br/>
        <label>Password</label>
        <input type="password" name="password" onChange={props.saveText}/>
        <br/>
        <input type="submit" value="Submit" />
        </form>
      </div>
      <div className="login-footer">
        <a href="#" onClick={props.loadSign}>
            Sign up
        </a>
      </div>
    </div>
  );



}


export default class login extends Component{

  constructor(props){
    super(props);
    this.state={
      username:"",
      password:"",
      clicked:true,
    }
    this.handleSignupClick= this.handleSignupClick.bind(this);
    this.ChangeHandler=this.ChangeHandler.bind(this);
  }

  handleSignupClick() {

    this.setState(state => ({

      clicked: !state.clicked
    }));

  }


  ChangeHandler(event){
    let nam = event.target.name;
    let val = event.target.value;

    this.setState(state =>({
      [nam]:val
    }));


  }

  render(){

  return (
      <div>
        {this.state.clicked ?  <LoadLogin loadSign={this.handleSignupClick} saveText={this.ChangeHandler} /> :<LoadSign/>}    
      </div>


  );

  }

}
