import React, { Component, StyleSheet } from 'react';
import './home.css';
import logo from './logo.svg';


function EmptyP(props){


    return(

        <div className="posts-empty"> 
            <span>NO HAY POSTS</span>
        </div>


    );

}

 function Post(props){


    return(

        <div className="post" key={props.post.key}>
            <div className='post-user'>{props.post.username}</div>
            <div className='post-date'>{props.post.date}</div>
            <div className='post-post'>{props.post.post}</div>
        </div>

    );
}


function Posts(props){


    // var url = 'http://localhost:4500/posts.php';


    const data=props.datas;
    if(data.length<1 || data.type==undefined)
    {
        return (
            <div>
                <EmptyP/>
            </div>            
        );
    }



    return(    

        <div>
        {props.data.map(p => <Post post={p} key={p.key}/>)}

        </div>



    );
}

function LoadHome(props){

    return(
        <div className="home">
        <div className="home-header">
          <h2>ReactBook</h2>
          <img src={logo} className="App-logo" alt="logo" />   

        </div>
        <div className="home-body">
            <div className="home-body-header"> 
                <div>Options</div> 
                <div>Log out</div>
           </div>
            <div className="home-body-newpost"> 
                <div className="newpost">           
                        <textarea  rows="6" cols="30" className="newpost-area"/>
                        <div className="newpost-options">
                            <div><button type="button">Post</button></div> 
                            <div><button type="button">Clear</button></div>                    
                        </div>
                </div> 
            </div>

                <Posts data={props.datas}/>


        </div>
        <div className="home-footer">

        </div>
      </div>
);

}







export default class home extends Component {


    constructor(props){
        super(props);
        this.state={
          loading:false,
          access:'denied',
          post:'',
          username:"",
          posts:[],      
          clicked:true,

        }
        // this.handleSignupClick= this.handleSignupClick.bind(this);
        // this.ChangeHandler=this.ChangeHandler.bind(this);
        // this.sendLogin=this.sendLogin.bind(this);
      }

      componentDidMount(){
          this.getPosts();
      }

    async getPosts(){
        var url = 'http://localhost:4500/posts.php';

        // var url = 'http://172.19.0.3:4500/login.php';

        // const req= await fetch(url, 

        this.setState({loading:true})    


        await fetch(url, {   

            method: 'POST',
            headers: 
            {
                'Access-Control-Allow-Origin': 'http://localhost:4500',
                'Accept': 'application/json',//'text/plain',
                'Connection': 'keep-alive',
            },
            mode:'cors',
            Connection:'keep',

        })
        .then(response => response.json()
        .then(res=>{


              this.setState({posts:res});     
              this.setState({loading:true})
              console.log(this.state.posts);        
         }))
         .catch(error => {
          var res={};
          this.setState({loading:true})    
          this.setState({posts:null});     
        });

    }


    render(){
        return (

            <div>
                <LoadHome datas={this.state.posts}/>
            </div>



        );
    }

};
