import React, { Component, StyleSheet } from 'react';
import './home.css';
import logo from './logo.svg';


function EmptyP(props){


    return(

        <div className="posts-empty"> 
            <span>NO HAY POSTS</span>
        </div>


    );

}

 function Post(props){


    return(

        <div className="post" key={props.post.key}>
            <div className='post-user'>{props.post.name}</div>
            <div className='post-date'>{props.post.date}</div>
            <div className='post-post'>{props.post.mess}</div>
        </div>

    );
}


function Posts(props){


    var url = 'http://localhost:4500/posts.php';


    //const ex=[];
    if([props.datas].length<1)
    {
        return (
            <div>
                <EmptyP/>
            </div>            
        );
    }
    const numbers = [1, 2, 3, 4, 5];
    const ex=[{name: "item1", date: 1, mess:"hola amigo 1",key:"key1"},
    {name: "item2", date: 2, mess:"hola amigo 2",key:"key2"},
     {name: "item3", date: 3,  mess:"hola amigo 3",key:"key3"}]
    return(



        // {[props.datas].map(p => <Post post={p} key={p.key}/>)}
        numbers.map((number) =>
            <div key={number.toString()}>
                {number}
            </div>

            )

    );
}

function LoadHome(props){

    return(
        <div className="home">
        <div className="home-header">
          <h2>ReactBook</h2>
          <img src={logo} className="App-logo" alt="logo" />   

        </div>
        <div className="home-body">
            <div className="home-body-header"> 
                <div>Options</div> 
                <div>Log out</div>
           </div>
            <div className="home-body-newpost"> 
                <div className="newpost">           
                        <textarea  rows="6" cols="30" className="newpost-area"/>
                        <div className="newpost-options">
                            <div><button type="button">Post</button></div> 
                            <div><button type="button">Clear</button></div>                    
                        </div>
                </div> 
            </div>

                <Posts datas={props.postsArray}/>


        </div>
        <div className="home-footer">

        </div>
      </div>
);

}







