import React, {Component} from 'react';
import './login.css';
import logo from './logo.svg';
import Signup,{signup} from './signup';


function LoadSign(props){


  return (

    <Signup/>

    );
}



function LoadLogin(props){





  return (      
    <div className="login">
      <div className="login-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h2>LOGIN</h2>    
      </div>
      <div className="login-body">
        <form action="localhost:4500/login.php" method="post">
        <label>Username</label>
        <input type="text" name="username" onChange={props.saveText}/>
        <br/>
        <label>Password</label>
        <input type="password" name="password" onChange={props.saveText}/>
        <br/>
        <input type="submit" value="Submit" onClick={e => props.sendLog(e)} />
        </form>
      </div>
      <div className="login-footer">
        <a href="#" onClick={props.loadSign}>
            Sign up
        </a>
      </div>
    </div>
  );



}


export default class login extends Component{

  constructor(props){
    super(props);
    this.state={
      username:"",
      password:"",
      clicked:true,
    }
    this.handleSignupClick= this.handleSignupClick.bind(this);
    this.ChangeHandler=this.ChangeHandler.bind(this);
    this.sendLogin=this.sendLogin.bind(this);
  }

  sendLogin(event){

    let formData = new FormData();
    formData.append('name', this.state.name)
    formData.append('password', this.state.password)

    event.preventDefault();
    console.log(this.state.username);

    var url = 'http://web_1:4500/login.php';

    fetch(url, { method: 'POST', body: formData })
    .then(function (response) {
      return response.text();
    })
    .then(function (body) {
      console.log(body);
    });


  };

  handleSignupClick() {

    this.setState(state => ({

      clicked: !state.clicked
    }));

  }


  ChangeHandler(event){



    this.setState({
      [event.target.name]:event.target.value
    })


  }

  render(){

  return (
      <div>
        {this.state.clicked ?  <LoadLogin loadSign={this.handleSignupClick} saveText={this.ChangeHandler} sendLog={this.sendLogin} /> :<LoadSign/>}    
      </div>


  );

  }

}
